CREATE TABLE app_users (
    id INT IDENTITY(1,1) PRIMARY KEY,
    email VARCHAR(1000) UNIQUE,
    username VARCHAR(1000) UNIQUE,
    password VARCHAR(1000),
    files_uploaded VARCHAR(255)
);
CREATE TABLE household_data (
    HSHD_NUM INT PRIMARY KEY,
    L VARCHAR(255),
    AGE_RANGE VARCHAR(50),
    MARITAL VARCHAR(50),
    INCOME_RANGE VARCHAR(50),
    HOMEOWNER VARCHAR(50),
    HSHD_COMPOSITION VARCHAR(255),
    HH_SIZE VARCHAR(10),
    CHILDREN VARCHAR(10)
);
CREATE TABLE product_data (
    PRODUCT_NUM INT PRIMARY KEY,
    DEPARTMENT VARCHAR(50),
    COMMODITY VARCHAR(50),
    BRAND_TY VARCHAR(50),
    NATURAL_ORGANIC_FLAG VARCHAR(1)
);
CREATE TABLE transaction_data (
    BASKET_NUM INT,
    HSHD_NUM INT,
    PURCHASE_DATE DATE,
    PRODUCT_NUM INT,
    SPEND MONEY,
    UNITS INT,
    STORE_R VARCHAR(50),
    WEEK_NUM INT,
    YEAR INT,
    FOREIGN KEY (HSHD_NUM) REFERENCES household_data(HSHD_NUM),
    FOREIGN KEY (PRODUCT_NUM) REFERENCES product_data(PRODUCT_NUM)
);
